CKEDITOR.editorConfig=function(e){lang=$("html").attr("lang"),e.toolbar="MyToolbar",e.defaultLanguage=lang,e.height=500,e.format_tags="p;h1;h2;h3;h4;h5;h6",e.forcePasteAsPlainText=!0,e.toolbar_MyToolbar=[{name:"document",items:["Maximize","-","NewPage","Preview"]},{name:"clipboard",items:["Cut","Copy","Paste","PasteFromWord","-","Undo","Redo"]},{name:"editing",items:["Find","Replace","-","SelectAll","-","Scayt","-","SpecialChar"]},{name:"styles",items:["Format","Bold","Italic","Strike","-","RemoveFormat"]},{name:"types",items:["NumberedList","BulletedList","-","Link","Unlink"]}]};